/**
     * 增加或者减少${table.description}
     */
    public void add${table.alias}(int ${table.name}) {
        this.${table.name} += ${table.name};
}

/**
     *  添加${table.description}
     */
    public void addRecord(int id) {
        ${table.name}.add(id);
    }
/**
     *  尝试添加${table.description}
     */
    public boolean tryAddRecord(int id) {
        if (${table.name}.contains(id)) {
            return false;
        }
        return ${table.name}.add(id);
    }
/**
     *  删除${table.description}
     */
    public void dropRecord(int id) {
        ${table.name}.remove(id);
    }

/**
     *  获取对象信息
     */
    public ${table.value} get${table.key}(int key) {
        return ${table.name}.computeIfAbsent(key, k -> ${table.value}.valueOf(key));
    }
/**
     *  获取
     */
    public int getValue(int key) {
        return ${table.name}.getOrDefault(key, 0);
    }
/**
     *  获取
     */
    public ${table.value} getValue(int key) {
        return ${table.name}.get(key);
    }
/**
     *  添加次数
     */
    public void addValue(int key, int value) {
        ${table.name}.merge(key, value, Integer::sum);
}

/**
     *  更新
     */
    public void update(int key, int value) {
        ${table.name}.put(key, value);
}
